// Run for ALL items to find one match
const webhookBody = $('Webhook').item.json.body;
const targetPassword = webhookBody.password ? webhookBody.password.toString().trim() : '';

let authorized = false;
let foundUser = null;
let reason = 'User not found';

// Loop through all items
for (const item of items) {
  const notionItem = item.json;

  if (!notionItem.id) continue;

  let storedPassword = '';

  // STRATEGY 1: N8N Simplified Mode
  if (notionItem['property_password']) {
      storedPassword = notionItem['property_password'];
  }
  // STRATEGY 2: Flattened regular
  else if (notionItem['Password']) {
      storedPassword = notionItem['Password'];
  }
  // STRATEGY 3: Nested Properties
  else if (notionItem.properties && notionItem.properties.Password) {
      if (notionItem.properties.Password.rich_text && notionItem.properties.Password.rich_text.length > 0) {
          storedPassword = notionItem.properties.Password.rich_text[0].plain_text;
      } else if (typeof notionItem.properties.Password === 'string') {
          storedPassword = notionItem.properties.Password;
      }
  }

  const cleanStored = storedPassword ? storedPassword.toString().trim() : '';

  // Compare
  if (cleanStored === targetPassword) {
    authorized = true;
    foundUser = notionItem;
    break;
  } else {
     reason = 'Invalid Username or Password';
  }
}

if (authorized) {
  return [{ json: { authorized: true, user: foundUser } }];
} else {
  return [{ json: { authorized: false, reason: reason } }];
}

const crypto = require('crypto');

// Get context from Webhook node
// Try different ways to access webhook data safely
let body = {};
let requestHeaders = {};

try {
    const webhookData = $('Webhook').first().json;
    body = webhookData.body || {};
    requestHeaders = webhookData.headers || {};
} catch(e) {
    // Fallback if direct access fails (e.g. testing)
    body = $json.body || {};
}

const email = body.email;
const secret = 'BLOOM_AUTH_SECRET_V1'; 
const expires = Date.now() + 3600000; // 1 hour

const payload = Buffer.from(JSON.stringify({ email, expires })).toString('base64');
const signature = crypto.createHmac('sha256', secret).update(payload).digest('hex');
const token = `${payload}.${signature}`;

// Determine Frontend URL dynamically from Origin header
let frontendUrl = 'http://localhost:5173';
if (requestHeaders && requestHeaders.origin) {
    frontendUrl = requestHeaders.origin;
}

const resetLink = `${frontendUrl}/reset-password?token=${token}&email=${email}`;

// Construct Email Body
const emailBody = `
<div style="font-family: Arial, sans-serif; padding: 20px; color: #333; max-width: 600px; margin: 0 auto; border: 1px solid #eee; border-radius: 8px;">
    <h2 style="color: #ff4d4d;">Recupero Password</h2>
    <p>Ciao,</p>
    <p>Hai richiesto di reimpostare la tua password per il tuo account Bloom AI.</p>
    <p>Clicca sul pulsante qui sotto per procedere:</p>
    <div style="text-align: center; margin: 30px 0;">
        <a href="${resetLink}" style="background-color: #ff4d4d; color: white; padding: 12px 24px; text-decoration: none; border-radius: 5px; font-weight: bold; display: inline-block;">Reimposta Password</a>
    </div>
    <p style="font-size: 14px; color: #666;">Se il pulsante non funziona, copia e incolla questo link nel tuo browser:</p>
    <p style="font-size: 12px; color: #888; word-break: break-all;">${resetLink}</p>
    <hr style="border: none; border-top: 1px solid #eee; margin: 20px 0;">
    <p style="font-size: 12px; color: #aaa;">Se non hai richiesto tu il ripristino, ignora questa email.</p>
</div>
`;

return [{ 
    json: { 
        token, 
        resetLink, 
        email,
        emailBody
    } 
}];

