    const webhookData = $('Webhook').first().json;
    body = webhookData.body || {};
} catch(e) {
    body = $json.body || {};
}

const token = body.token;
// Email might come from body param or encoded in token
// For verification logic we use body email if present, or extract from token
let email = body.email;

// Password might be passed as 'password' or 'new_password'
const newPassword = body.password || body.new_password;

if (!token || !token.includes('.')) throw new Error('Invalid Token Format');

const [payloadB64, signature] = token.split('.');
const secret = 'BLOOM_AUTH_SECRET_V1';
const expectedSig = crypto.createHmac('sha256', secret).update(payloadB64).digest('hex');

if (signature !== expectedSig) throw new Error('Invalid Token Signature');

const payload = JSON.parse(Buffer.from(payloadB64, 'base64').toString('utf-8'));

if (Date.now() > payload.expires) throw new Error('Token Expired');

// If email provided in body, check match. Otherwise verify token email is valid.
if (email && payload.email !== email) throw new Error('Email Mismatch');

// Use email from token payload as the source of truth
email = payload.email;

return [{ 
    json: { 
        email, 
        newPassword,
        valid: true
    } 
}];

